import React, { useState } from 'react';

const styles = {
  container: {
    display: 'flex',
    minHeight: '100vh',
    fontFamily: "'Segoe UI', sans-serif",
  },
  sidebar: {
    backgroundColor: '#1e293b',
    color: '#fff',
    width: '240px',
    padding: '2rem 1rem',
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  sidebarHeader: {
    fontSize: '1.5rem',
    marginBottom: '2rem',
  },
  button: {
    background: 'none',
    border: 'none',
    padding: '0.7rem 1rem',
    textAlign: 'left',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background 0.2s',
    borderRadius: '5px',
  },
  buttonHover: {
    backgroundColor: '#3187ffff',
  },
  content: {
    flex: 1,
    padding: '2rem',
    backgroundColor: '#a8b4c0ff',
  },
};

const User = () => {
  const [activeTab, setActiveTab] = useState('checkup');
  const [hovered, setHovered] = useState(null);

  const renderContent = () => {
    switch (activeTab) {
      case 'checkup':
        return (
          <div>
            <h2>COVID Risk Checkup</h2>
            <p>Form inputs: City, symptoms, travel history, etc.</p>
          </div>
        );
      case 'history':
        return (
          <div>
            <h2>My Predictions History</h2>
            <p>Display previous predictions here.</p>
          </div>
        );
      case 'contacts':
        return (
          <div>
            <h2>Emergency Contacts</h2>
            <p>List of emergency numbers, hospital contacts, etc.</p>
          </div>
        );
      default:
        return null;
    }
  };

  const renderButton = (tab, label) => (
    <button
      onClick={() => setActiveTab(tab)}
      onMouseEnter={() => setHovered(tab)}
      onMouseLeave={() => setHovered(null)}
      style={{
        ...styles.button,
        ...(hovered === tab ? styles.buttonHover : {}),
      }}
    >
      {label}
    </button>
  );

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <div style={styles.sidebarHeader}>User Dashboard</div>
        {renderButton('checkup', 'COVID Risk Checkup')}
        {renderButton('history', 'My Predictions History')}
        {renderButton('contacts', 'Emergency Contacts')}
      </aside>
      <main style={styles.content}>{renderContent()}</main>
    </div>
  );
};

export default User;
