// src/components/BookAppointment.jsx
import React, { useEffect, useState } from 'react';
import { db, auth } from '../firebase';
import { collection, getDocs, addDoc } from 'firebase/firestore';

const BookAppointment = () => {
  const [doctors, setDoctors] = useState([]);

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "doctors"));
        const docList = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setDoctors(docList);
      } catch (error) {
        console.error("Error fetching doctors:", error);
      }
    };
    fetchDoctors();
  }, []);

  const handleBook = async (doctor) => {
    const user = auth.currentUser;
    if (!user) return alert("Please login to book an appointment.");

    const appointment = {
      userId: user.uid,
      userName: user.displayName || "Anonymous",
      doctorId: doctor.id,
      doctorName: doctor.name,
      date: new Date().toISOString().split("T")[0],
      time: "10:00", // Optional: make this selectable
      status: "pending"
    };

    try {
      await addDoc(collection(db, "appointments"), appointment);
      alert("Appointment request sent!");
    } catch (error) {
      console.error("Error booking appointment:", error);
    }
  };

  return (
    <div className="appointment-container">
      <h2>Book an Appointment</h2>
      {doctors.length === 0 && <p>No doctors available right now.</p>}
      {doctors.map(doctor => (
        <div className="doctor-card" key={doctor.id}>
          <h4>{doctor.name}</h4>
          <p>{doctor.specialization}</p>
          <button onClick={() => handleBook(doctor)}>Book Appointment</button>
        </div>
      ))}
    </div>
  );
};

export default BookAppointment;

