import React, { useState } from 'react';
import RiskPredictor from './RiskPredictor';
import EmergencyContact from './EmergencyContact'; // âœ… Make sure this file exists and exports the component

const styles = {
  container: {
    display: 'flex',
    minHeight: '100vh',
    fontFamily: "'Segoe UI', sans-serif",
  },
  sidebar: {
    backgroundColor: '#1e293b',
    color: '#fff',
    width: '240px',
    padding: '2rem 1rem',
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  sidebarHeader: {
    fontSize: '1.5rem',
    marginBottom: '2rem',
    fontWeight: 'bold',
    borderBottom: '1px solid #334155',
    paddingBottom: '1rem',
  },
  button: {
    background: 'none',
    border: 'none',
    padding: '0.7rem 1rem',
    textAlign: 'left',
    fontSize: '1rem',
    color:' #ffffffff',
    transition: 'background 0.2s',
    borderRadius: '5px',
  },
  buttonHover: {
    backgroundColor: '#3187ff',
  },
  activeButton: {
    backgroundColor: '#2563eb',
  },
  content: {
    flex: 1,
    padding: '2rem',
    backgroundColor: '#e2e8f0',
  },
};

const User = () => {
  const [activeTab, setActiveTab] = useState('checkup');
  const [hovered, setHovered] = useState(null);

  const renderContent = () => {
    switch (activeTab) {
      case 'checkup':
        return (
          <div>
            <h2>COVID Risk Checkup</h2>
            <RiskPredictor />
          </div>
        );
      case 'history':
        return (
          <div>
            <h2>My Predictions History</h2>
            <p>Display user's prediction history here.</p>
          </div>
        );
      case 'contacts':
        return (
          <div>
            <h2>Emergency Contacts</h2>
            <EmergencyContact />
          </div>
        );
      default:
        return null;
    }
  };

  const renderButton = (tab, label) => (
    <button
      onClick={() => setActiveTab(tab)}
      onMouseEnter={() => setHovered(tab)}
      onMouseLeave={() => setHovered(null)}
      style={{
        ...styles.button,
        ...(hovered === tab ? styles.buttonHover : {}),
        ...(activeTab === tab ? styles.activeButton : {}),
      }}
    >
      {label}
    </button>
  );

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <div style={styles.sidebarHeader}>User Dashboard</div>
        {renderButton('checkup', 'ðŸ§ª COVID Risk Checkup')}
        {renderButton('history', 'ðŸ“œ My Predictions History')}
        {renderButton('contacts', 'ðŸš¨ Emergency Contacts')}
      </aside>
      <main style={styles.content}>{renderContent()}</main>
    </div>
  );
};

export default User;
