import React, { useState } from 'react';
import CommunityMapSection from './CommunityTrendChart';
import LocalTransmissionChart from './LocalTransmissionChart';
const styles = {
  page: {
    fontFamily: "'Segoe UI', sans-serif",
    padding: '2rem',
    backgroundColor: '#f9fafb',
  },
  hero: {
    backgroundColor: '#e0f2fe',
    padding: '2rem',
    borderRadius: '10px',
    marginBottom: '2rem',
  },
  inputGroup: {
    marginBottom: '1rem',
  },
  label: {
    display: 'block',
    marginBottom: '0.3rem',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '0.6rem',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '0.7rem 1.5rem',
    backgroundColor: '#0ea5e9',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    marginTop: '1rem',
  },
  section: {
    backgroundColor: '#ffffff',
    borderRadius: '10px',
    padding: '1.5rem',
    marginBottom: '2rem',
    boxShadow: '0 0 10px rgba(0,0,0,0.05)',
  },
  riskText: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
  },
  suggestion: {
    marginTop: '1rem',
    color: '#334155',
  },
  mapPlaceholder: {
    height: '300px',
    backgroundColor: '#cbd5e1',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontStyle: 'italic',
    color: '#475569',
    borderRadius: '8px',
  },
};

const RiskPredictor = () => {
  const [formData, setFormData] = useState({
    city: '',
    symptoms: '',
    travel: '',
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

const handleRiskCheck = () => {
  const { city, symptoms, travel } = formData;

  // Basic validation
  if (!city.trim() || !symptoms.trim() || !travel.trim()) {
    alert('Please fill in all fields before checking your risk.');
    return;
  }

  // Simulated logic
  const riskScore = Math.random();
  const level =
    riskScore < 0.4 ? 'Low' : riskScore < 0.75 ? 'Medium' : 'High';

  setResult({
    level,
    message:
      level === 'High'
        ? 'Get tested and isolate immediately.'
        : level === 'Medium'
        ? 'Monitor symptoms and avoid crowded areas.'
        : 'Stay safe, keep following guidelines.',
  });
};



  return (
    <div style={styles.page}>
      {/* Hero Section */}
      <div style={styles.hero}>
        <h2>COVID Risk Checkup</h2>
        <div style={styles.inputGroup}>
          <label style={styles.label}>City</label>
          <input
            name="city"
            type="text"
            placeholder="Enter your city"
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Symptoms</label>
          <input
            name="symptoms"
            type="text"
            placeholder="Fever, cough, etc."
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Recent Travel History</label>
          <input
            name="travel"
            type="text"
            placeholder="E.g. Delhi, Mumbai"
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <button style={styles.button} onClick={handleRiskCheck}>
          Check My Risk
        </button>
      </div>

      {/* Prediction Result */}
      {result && (
        <div style={styles.section}>
          <h3>Prediction Result</h3>
          <p style={styles.riskText}>Risk Level: {result.level}</p>
          <p style={styles.suggestion}>{result.message}</p>
        </div>
      )}

      {/* Graph Section */}
      <div style={styles.section}>
        <CommunityMapSection />

      </div>

      {/* Map Section */}
      <div style={styles.section}>
        <LocalTransmissionChart />

      </div>

      {/* WHO Guidelines / News */}
<div style={styles.section}>
  <h3 style={{ fontSize: '1.5rem', color: '#333', marginBottom: '1rem' }}>üßæ WHO Guidelines & Latest News</h3>
  <ul style={{ listStyle: 'none', padding: 0, lineHeight: '2' }}>
    <li>
      üì∞ <a href="https://www.who.int/emergencies/diseases/novel-coronavirus-2019" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{ color: '#1a73e8', textDecoration: 'none' }}>
            Stay updated with official WHO COVID-19 news
          </a>
    </li>
    <li>
      üíâ <a href="https://www.who.int/news-room/questions-and-answers/item/coronavirus-disease-(covid-19)-vaccines" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{ color: '#34a853', textDecoration: 'none' }}>
            COVID-19 Vaccination: Recommended every 6 months
          </a>
    </li>
    <li>
      üìç <a href="https://travel.state.gov/content/travel/en/traveladvisories/traveladvisories.html" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{ color: '#ea4335', textDecoration: 'none' }}>
            Avoid travel to outbreak-prone regions
          </a>
    </li>
    <li>
      üßº <a href="https://www.who.int/emergencies/diseases/novel-coronavirus-2019/advice-for-public" 
            target="_blank" 
            rel="noopener noreferrer"
            style={{ color: '#fbbc04', textDecoration: 'none' }}>
            Practice hygiene: Wash hands, wear masks
          </a>
    </li>
  </ul>
</div>

    </div>
  );
};

export default RiskPredictor;
