export const calculateRisk = ({ city, symptoms, travel }) => {
  let score = 0;
  if (symptoms.toLowerCase().includes('fever')) score += 2;
  if (symptoms.toLowerCase().includes('cough')) score += 2;
  if (travel) score += 3;
  if (city.toLowerCase() === 'delhi') score += 1;

  return score >= 5 ? 'High' : score >= 3 ? 'Medium' : 'Low';
};
