import React, { useState } from 'react';
import { calculateRisk } from '../utils/calculateRisk';

const PublicRiskCheck = () => {
  const [city, setCity] = useState('');
  const [symptoms, setSymptoms] = useState('');
  const [travel, setTravel] = useState(false);
  const [risk, setRisk] = useState(null);

  const handleSubmit = () => {
    const score = calculateRisk({ city, symptoms, travel });
    setRisk(score);
  };

  return (
    <div>
      <h2>Risk Predictor</h2>
      <input placeholder="City" onChange={e => setCity(e.target.value)} />
      <input placeholder="Symptoms" onChange={e => setSymptoms(e.target.value)} />
      <label>
        Travel History <input type="checkbox" onChange={e => setTravel(e.target.checked)} />
      </label>
      <button onClick={handleSubmit}>Check My Risk</button>
      {risk !== null && <p>Risk Score: {risk}</p>}
    </div>
  );
};

export default PublicRiskCheck
