import React, { useState } from 'react';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import RoleSwitcher from './components/RoleSwitcher';
import AdminDashboard from './pages/AdminDashboard';
import PublicRiskCheck from './pages/PublicRiskCheck';
import ChatBot from './pages/ChatBot';
import MultiRoleDashboard from './pages/MultiRoleDashboard';

const App = () => {
  const [currentPage, setCurrentPage] = useState('admin');

  const renderPage = () => {
    switch (currentPage) {
      case 'public': return <PublicRiskCheck />;
      case 'chat': return <ChatBot />;
      case 'multi': return <MultiRoleDashboard />;
      default: return <AdminDashboard />;
    }
  };

  return (
    <div>
      <Header />
      <Sidebar onNavigate={setCurrentPage} />
      <RoleSwitcher setPage={setCurrentPage} />
      <div className="container">
        {renderPage()}
      </div>
    </div>
  );
};

export default App;
