import React, { useState } from 'react';

const styles = {
  page: {
    fontFamily: "'Segoe UI', sans-serif",
    padding: '2rem',
    backgroundColor: '#f9fafb',
  },
  hero: {
    backgroundColor: '#e0f2fe',
    padding: '2rem',
    borderRadius: '10px',
    marginBottom: '2rem',
  },
  inputGroup: {
    marginBottom: '1rem',
  },
  label: {
    display: 'block',
    marginBottom: '0.3rem',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '0.6rem',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '0.7rem 1.5rem',
    backgroundColor: '#0ea5e9',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    marginTop: '1rem',
  },
  section: {
    backgroundColor: '#ffffff',
    borderRadius: '10px',
    padding: '1.5rem',
    marginBottom: '2rem',
    boxShadow: '0 0 10px rgba(0,0,0,0.05)',
  },
  riskText: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
  },
  suggestion: {
    marginTop: '1rem',
    color: '#334155',
  },
  mapPlaceholder: {
    height: '300px',
    backgroundColor: '#cbd5e1',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontStyle: 'italic',
    color: '#475569',
    borderRadius: '8px',
  },
};

const RiskPredictor = () => {
  const [formData, setFormData] = useState({
    city: '',
    symptoms: '',
    travel: '',
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

const handleRiskCheck = () => {
  const { symptoms, travel, city } = formData;

  let riskScore = 0;

  // Step 1: Evaluate symptoms
  const symptomList = symptoms.toLowerCase();
  if (symptomList.includes('fever')) riskScore += 0.4;
  if (symptomList.includes('cough')) riskScore += 0.3;
  if (symptomList.includes('breath') || symptomList.includes('breathing')) riskScore += 0.3;
  if (symptomList.includes('fatigue')) riskScore += 0.2;
  if (symptomList.includes('headache')) riskScore += 0.1;

  // Step 2: Evaluate travel
  if (travel.trim()) riskScore += 0.2;

  // Step 3: Evaluate high-risk cities (example)
  const highRiskCities = ['delhi', 'mumbai', 'bangalore'];
  if (highRiskCities.includes(city.toLowerCase())) riskScore += 0.2;

  // Cap score at 1
  riskScore = Math.min(riskScore, 1);

  const level =
    riskScore < 0.4 ? 'Low' : riskScore < 0.75 ? 'Medium' : 'High';

  setResult({
    level,
    message:
      level === 'High'
        ? 'High risk: Please isolate and get tested immediately.'
        : level === 'Medium'
        ? 'Medium risk: Monitor symptoms and stay cautious.'
        : 'Low risk: Stay safe and continue precautions.',
  });
};


  return (
    <div style={styles.page}>
      {/* Hero Section */}
      <div style={styles.hero}>
        <h2>COVID Risk Checkup</h2>
        <div style={styles.inputGroup}>
          <label style={styles.label}>City</label>
          <input
            name="city"
            type="text"
            placeholder="Enter your city"
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Symptoms</label>
          <input
            name="symptoms"
            type="text"
            placeholder="Fever, cough, etc."
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Recent Travel History</label>
          <input
            name="travel"
            type="text"
            placeholder="E.g. Delhi, Mumbai"
            style={styles.input}
            onChange={handleChange}
          />
        </div>
        <button style={styles.button} onClick={handleRiskCheck}>
          Check My Risk
        </button>
      </div>

      {/* Prediction Result */}
      {result && (
        <div style={styles.section}>
          <h3>Prediction Result</h3>
          <p style={styles.riskText}>Risk Level: {result.level}</p>
          <p style={styles.suggestion}>{result.message}</p>
        </div>
      )}

      {/* Graph Section */}
      <div style={styles.section}>
        <h3>Community Transmission Trend</h3>
        <div style={styles.mapPlaceholder}>
          [Graph Placeholder - Coming Soon]
        </div>
      </div>

      {/* Map Section */}
      <div style={styles.section}>
        <h3>Local Transmission Heatmap</h3>
        <div style={styles.mapPlaceholder}>
          [Heatmap, Test Centers, Vaccination Spots - Coming Soon]
        </div>
      </div>

      {/* WHO Guidelines / News */}
      <div style={styles.section}>
        <h3>Latest Updates & Guidelines</h3>
        <ul>
          <li>📰 WHO: Stay updated with official guidelines</li>
          <li>💉 Vaccination recommended every 6 months</li>
          <li>📍 Avoid travel to known outbreak areas</li>
          <li>🧼 Keep washing hands & wear masks</li>
        </ul>
      </div>
    </div>
  );
};

export default RiskPredictor;
