// src/components/User.jsx
import React, { useState } from 'react';
import './User.css';

const User = () => {
  const [activeTab, setActiveTab] = useState('checkup');

  const renderContent = () => {
    switch (activeTab) {
      case 'checkup':
        return (
          <div>
            <h2>COVID Risk Checkup</h2>
            <p>Form inputs: City, symptoms, travel history, etc.</p>
          </div>
        );
      case 'history':
        return (
          <div>
            <h2>My Predictions History</h2>
            <p>Display previous predictions here.</p>
          </div>
        );
      case 'contacts':
        return (
          <div>
            <h2>Emergency Contacts</h2>
            <p>List of emergency numbers, hospital contacts, etc.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="user-container">
      <aside className="user-sidebar">
        <h2>User Dashboard</h2>
        <button onClick={() => setActiveTab('checkup')}>COVID Risk Checkup</button>
        <button onClick={() => setActiveTab('history')}>My Predictions History</button>
        <button onClick={() => setActiveTab('contacts')}>Emergency Contacts</button>
      </aside>
      <main className="user-content">{renderContent()}</main>
    </div>
  );
};

export default User;
